import React from 'react'
import styled from 'styled-components'
import InfoOutlinedIcon from '@material-ui/icons/InfoOutlined';
import ChatInfo from './ChatInfo'
import ChatMessage from './ChatMessage'
import db from '../firebase'
import { useParams } from 'react-router-dom'

function chat() {
    let { ChanelId } = useParams();
    const getChanel =()=>{
        db.collection('rooms')
        .doc(ChanelId)
        .onSnapshot((snapshot)=>{
            console.log(snapshot.data())
        })

    }
    getChanel()
    return (
        <Container>
            <Header>
                <Chanel>
                    <ChanelName>
                        #Clever
                    </ChanelName>
                    <ChanelInfo>
                        Company-wide announcements and work based matters
                    </ChanelInfo>

                </Chanel>
                <ChanelDetails>
                    <div>Details</div>
                    <Info />
                     
                </ChanelDetails>

            </Header>
            <MessageContainer>
                <ChatMessage />
            </MessageContainer>
            <ChatInfo />

        </Container>
    )
}

export default chat
const Container = styled.div`
    display: grid;
    grid-template-rows: 65px auto min-content;
`
const Header =styled.div`
    padding-left: 20px;
    paading-right: 20px;
    display: flex;
    align-items: center;
    border-bottom: solid 1px rgb(83, 39, 83, .13);
    justify-content: space-between;

`
const Chanel = styled.div`

`
const ChanelName = styled.div`
    font-wight: 600;
`
const ChanelInfo = styled.div`
    font-weight: 400;
    color: #606060;
    font-size: 13px;
    margin-top: 8px;

`
const ChanelDetails = styled.div`
    display: flex;
    align-items: center;
`
const MessageContainer =styled.div`

`
const Info =styled(InfoOutlinedIcon)`
    margin-left: 10px;
`
